<?phpnamespace hypeJunction\Categories;use ElggFile;use ElggObject;$config = get_input('categories');$hierarchy = json_decode(get_input('hierarchy'), true);                     //$hierarchy = $config['hierarchy'];$root_guid = get_input('container_guid', elgg_get_site_entity()->guid);      $display .= '$root_guid = '.$root_guid.'<br>';$deleted = $success = $error = $empty = 0;foreach ($config['hierarchy'] as $key => $node_id) {                      //$display .= '15 $key['.$key.'] = '.$node_id.'<br>';	$guid = (int) $config['guid'][$key];                                  //$display .= '17 $guid = '.$guid.'<br>';	$title = $config['title'][$key];                                      //$display .= '18 $title = '.$title.'<br>';	$desc = $config['description'][$key];	$container_guid = (int) $config['container_guid'][$key];              //$display .= '20 $container_guid = '.$container_guid.'<br>';	$access_id = $config['access_id'][$key];	$icon = '';	if ($guid && ($category = get_entity($guid))) {		if (!$title) {			$category->delete();			$category = '';			$deleted++;		} else {			$category->title = $title;                                     //$display .= '$title = '.$title.'<br>';			$category->description = $desc;//			$category->container_guid = $container_guid;			$category->access_id = $access_id;			$category->priority = $key;			if ($category->save()) {				$success++;			} else {				$error++;			}		}	} else if ($title) {		$category = new ElggObject();		$category->subtype = HYPECATEGORIES_SUBTYPE;		$category->owner_guid = elgg_get_logged_in_user_guid();//		$category->container_guid = $root_guid;		$category->title = $title;		$category->description = $desc;		$category->access_id = $access_id;		$category->priority = $key;		if ($category->save()) {			$success++;		} else {			$error++;		}	} else {		$category = '';		$empty++;	}	if (elgg_instanceof($category) && !empty($_FILES['categories']['name']['icon'][$key])) {		$icontime = false;		$icon_sizes = elgg_get_config('icon_sizes');		$prefix = "icons/" . $category->getGUID();		foreach ($icon_sizes as $size => $values) {			$thumb_resized = get_resized_image_from_existing_file($_FILES['categories']['tmp_name']['icon'][$key], $values['w'], $values['h'], $values['square'], 0, 0, 0, 0, $values['upscale']);			if ($thumb_resized) {				$thumb = new ElggFile();				$thumb->owner_guid = $category->owner_guid;				$thumb->setMimeType('image/jpeg');				$thumb->setFilename($prefix . "$size.jpg");				$thumb->open("write");				$thumb->write($thumb_resized);				$thumb->close();				$icontime = true;			}		}		if ($icontime) {			$category->icontime = time();		}	}	$nodes[$node_id] = $category;                                                      $display .= '89 $nodes['.$node_id.'] = '.$category['title'].'<br>';}/**********///goto eof;/**********/if (is_array($hierarchy)){    foreach ($hierarchy as $key => $root) {                                                 $display .= '94 $hierarchy['.$key.'] => '.$root.'<br>';    	update_hierarchy($root['id'], $root['children'], $nodes);    }}function update_hierarchy($node_id, $children, $nodes) {	$category = $nodes[$node_id];	if ($children) {		foreach ($children as $child) {			$child_node_id = $child['id'];                                               //$display .= '$child_node_id: '.$child_node_id.'<br>';			$child_children = $child['children'];			$subcategory = $nodes[$child_node_id];			error_log("Parent: $node_id; Child: $child_node_id");			if ($category->guid == $subcategory->guid) {				continue;			}			if (elgg_instanceof($category) && elgg_instanceof($subcategory)) {				$subcategory->container_guid = $category->guid;                           $display .= '$subcategory->title = '.$subcategory->title.'<br>';                                                                            			  $display .= '$subcategory->guid = '.$subcategory->guid.'<br>';                                                                            			  $display .= '$subcategory->container_guid = '.$subcategory->container_guid.'<br>';				$subcategory->save();			} else if (!elgg_instanceof($category) && elgg_instanceof($subcategory)) {////				$subcategory->delete();			}			update_hierarchy($child_node_id, $child_children, $nodes);		}	}//	echo register_error($display);}//forward(REFERER);eof://echo register_error($display);