<?phpnamespace hypeJunction\Categories;$item = elgg_extract('item', $vars);$collapse = elgg_extract('collapse', $vars, false);$show_toggle = elgg_extract('show_toggle', $vars, true);$item_class = ($collapse) ? 'elgg-menu-closed' : 'elgg-menu-open';$children = $item->getChildren();if ($children) {	$item_class = "$item_class elgg-menu-parent";	$toggle = '<span class="elgg-child-menu-toggle"><span class="collapse ">&#9698;</span><span class="expand">&#9654;</span></span>';} else {	$item_class = "$item_class elgg-menu-nochildren";}$item_class = "$item_class {$item->getItemClass()}";if ($item->getSelected()) {	$item_class = "$item_class elgg-state-selected";}if (isset($vars['item_class']) && $vars['item_class']) {	$item_class .= ' ' . $vars['item_class'];}echo "<li class=\"$item_class\">";if ($show_toggle){	//@EDIT - 2017-10-03 - SAJ - Deprecated in 1.9: \ElggMenuItem::getContent() deprecated by elgg_view_menu_item()    //echo $toggle .'=>'. $item->getContent();	echo $toggle .'=>'. elgg_view_menu_item($item);}else {	//@EDIT - 2017-10-03 - SAJ - Deprecated in 1.9: \ElggMenuItem::getContent() deprecated by elgg_view_menu_item()	//echo $item->getContent();    echo elgg_view_menu_item($item);}if ($children) {	echo elgg_view('navigation/menu/categories/section', array(		'items' => $children,		'class' => 'elgg-menu elgg-child-menu',		'collapse' => true	));}echo '</li>';