<?phpnamespace hypeJunction\Gallery;$entity = elgg_extract('entity', $vars, false);$full = elgg_extract('full_view', $vars, false);$input_name = elgg_extract('input_name', $vars, false);$input_value_field = elgg_extract('input_value_field', $vars, false);$view_type = elgg_extract('list_type', $vars, 'full');if (!elgg_instanceof($entity, 'object', hjAlbumImage::SUBTYPE)) {	return true;}$subtitle = elgg_view('object/hjalbum/meta', array(	'entity' => $entity,	'full_view' => true		));$metadata = elgg_view_menu('entity', array(	'entity' => $entity,	'class' => 'elgg-menu-hz',	'sort_by' => 'priority'		));if ($input_name){    $input_tag = elgg_view('input/hidden', array('name'=>$input_name, 'value'=>$entity->$input_value_field));}if ($full) {	$body .= '<div class="gallery-media-full-view">';	$body .= elgg_view_entity_icon($entity, 'taggable', array(		'href' => false,		'img_class' => 'taggable'	));	$body .= '</div>';	$summary = elgg_view('object/hjalbumimage/meta', $vars);	$comments = elgg_view_comments($entity);	echo '<div class="gallery-media-full">';	echo "$body$summary$comments";	echo '</div>';} elseif($view_type == 'list_experience'){    $icon = elgg_view_entity_icon($entity, 'small');	$params = array(		'entity' => $entity,		'metadata' => $metadata,		'subtitle' => $subtitle,	    'tags'     => 'none'	);//	$params = $params + $vars;//	unset($params['metadata'], $params['tags']);	$list_body = elgg_view('object/elements/summary', $params);	$list_body = str_replace("elgg-body", "elgg-body quebx-menu-q", $list_body);	echo elgg_view_image_block($icon, $list_body, $params);}else {	$icon = elgg_view_entity_icon($entity, 'medium');	$params = array(		'entity'     => $entity,		'metadata'   => $metadata,		'subtitle'   => $subtitle,		'content'    => elgg_get_excerpt(elgg_strip_tags($entity->description)),		'body_class' => 'quebx-menu-q'	);	$params = $params + $vars;	$list_body = elgg_view('object/elements/summary', $params);	echo elgg_view_image_block($icon, $list_body, $params);}echo $input_tag;