<!-- Path: quebx\views\default\navigation\menu\categories.php --><?php$vars['name'] = preg_replace('/[^a-z0-9\-]/i', '-', $vars['name']);$headers = elgg_extract('show_section_headers', $vars, false);$item_class = elgg_extract('item_class', $vars);$class = "elgg-menu elgg-menu-{$vars['name']} {$vars['category_class']}";if (isset($vars['class'])) {	$class .= " {$vars['class']}";}foreach ($vars['menu'] as $section => $menu_items) {	echo elgg_view('navigation/menu/categories/section',            	  ['items'                => $menu_items,            	   'class'                => "$class elgg-menu-{$vars['name']}-$section",            	   'item_class'           => $item_class,            	   'section'              => $section,            	   'name'                 => $vars['name'],            	   'presentation'         => $vars['presentation'],            	   'perspective'          => $vars['perspective'],                   'parent_cid'           => $vars['parent_cid'],            	   'show_section_headers' => $headers]);}