<!-- Path: quebx\views\default\navigation\menu\categories\item.php --><?php$item = elgg_extract('item', $vars);$collapse = elgg_extract('collapse', $vars, true);$presentation = elgg_extract('presentation', $vars);$perspective = elgg_extract('perspective', $vars);$parent_cid  = elgg_extract('parent_cid', $vars);$item_class = ($collapse) ? 'elgg-menu-closed' : 'elgg-menu-open';$children = $item->getChildren();if ($children) {	$item_class = "$item_class elgg-menu-parent";//	$toggle = '<span class="elgg-child-menu-toggle"><span class="collapse ">[-]</span><span class="expand">[+]</span></span>';//	$toggle = '<span class="elgg-child-menu-toggle"><span class="collapse ">&#x25bd;</span><span class="expand">&#x25b7;</span></span>';	$toggle = '<span class="CategoryDropdownMenu___fa7PcH7A"><span class="collapse ">&#9698;</span><span class="expand">&#9654;</span></span>';} else {	$item_class = "$item_class elgg-menu-nochildren";}$item_class = "$item_class {$item->getItemClass()}";if ($item->getSelected()) {	$item_class = "$item_class elgg-state-selected";}/*if (isset($vars['item_class']) && $vars['item_class']) {	$item_class .= ' ' . $vars['item_class'];}*/if ($children) {	$section = elgg_view('navigation/menu/categories/section', 	                    ['items'       => $children,                 		 'class'       => 'elgg-menu elgg-child-menu',	                     'item_class'  => $vars['item_class'],	                     'presentation'=> $presentation,                         'perspective' => $perspective,                         'parent_cid'  => $parent_cid,                 		 'collapse'    => $collapse]); }$item->itemClass = $item_class;Switch($presentation){    case 'pallet':        echo elgg_format_element('li',                                  ['class'=>$item_class,],                                 elgg_format_element('div',                                                     ['class'=>$vars['item_class']],                                                      $toggle.elgg_view_menu_item($item)).                                 $section);        break;    default:                echo elgg_format_element('li',                                  ['class'=>$item_class,                                 ],                                 $toggle.elgg_view_menu_item($item).$section);    break;}