<!-- Path: quebx\views\default\navigation\menu\categories\section.php --><?php$headers = elgg_extract('show_section_headers', $vars, false);$class = elgg_extract('class', $vars, '');$item_class = elgg_extract('item_class', $vars, '');$id = elgg_extract('id', $vars);$collapse = elgg_extract('collapse', $vars, true);$show_toggle = elgg_extract('show_toggle', $vars, true);$presentation = elgg_extract('presentation', $vars);$perspective = elgg_extract('perspective', $vars);$parent_cid  = elgg_extract('parent_cid', $vars);Switch ($presentation){    case 'pallet':        foreach ($vars['items'] as $menu_item) {        	$items.= elgg_view('navigation/menu/categories/item', [        		'item'        => $menu_item,        		'item_class'  => $item_class,        		'collapse'    => $collapse,        	    'show_toggle' => $show_toggle,        	    'presentation'=> $presentation,                'perspective' => $perspective,                'parent_cid'  => $parent_cid,]);        }                echo elgg_format_element('ul', ['id'=> $id, 'class'=>$class], $items);                break;    default:       if ($headers) {        	$name = elgg_extract('name', $vars);        	$section = elgg_extract('section', $vars);        	echo '<h2>' . elgg_echo("menu:$name:header:$section") . '</h2>';        }        foreach ($vars['items'] as $menu_item) {        	$items.= elgg_view('navigation/menu/categories/item', [        		'item'        => $menu_item,        		'item_class'  => $item_class,        		'collapse'    => $collapse,        	    'show_toggle' => $show_toggle,        	    'presentation'=> $presentation,                'perspective' => $perspective,                'parent_cid'  => $parent_cid,]);        }                echo elgg_format_element('ul', ['id'=> $id, 'class'=>$class], $items);}